import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthServiceService } from '../auth-service.service';


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent implements OnInit {

constructor(private service: AuthServiceService,private route: Router){ }

  ngOnInit(): void {}


    formData = new FormData();

    // Validation error messages
    validationErrors: { [key: string]: string } = {};

    onSubmit(signupForm: any): void {
      if (signupForm.valid) {
        // Clear previous errors
        this.validationErrors = {};

        // Append all form fields to FormData
        Object.keys(signupForm.value).forEach((key) => {
          this.formData.append(key, signupForm.value[key]);
        });

        // Log form data for debugging
        console.log('Form Data:', Object.fromEntries(this.formData.entries()));

        // Example: Send form data to backend
        // this.http.post('your-api-endpoint', this.formData).subscribe(
        //   (response) => console.log('Success:', response),
        //   (error) => console.error('Error:', error)
        // );

        alert('Form submitted successfully!');
      } else {
        // Populate validation errors
        this.validationErrors = this.getValidationErrors(signupForm);
        console.error('Validation Errors:', this.validationErrors);
        alert('Please fix the highlighted errors before submitting.');
      }
    }

    /**
     * Generate validation error messages for invalid fields.
     * @param form The form object
     * @returns An object containing field-specific error messages
     */
    private getValidationErrors(form: any): { [key: string]: string } {
      const errors: { [key: string]: string } = {};

      for (const field in form.controls) {
        if (form.controls[field]?.invalid) {
          if (form.controls[field].errors?.['required']) {
            errors[field] = `${this.toReadableFieldName(field)} is required.`;
          } else if (form.controls[field].errors?.['email']) {
            errors[field] = `Please enter a valid email address.`;
          }
        }
      }

      return errors;
    }

    /**
     * Convert field names to a more readable format.
     * @param field The field name
     * @returns A readable field name
     */
    private toReadableFieldName(field: string): string {
      return field
        .replace(/([A-Z])/g, ' $1')
        .replace(/^./, (str) => str.toUpperCase());
    }
  }



