<div class="container mt-5 mb-5">
  <div class="card shadow-lg p-4">
    <h2 class="text-center mb-4" style="font-size: 20px; font-weight: 700;">Organization Signup</h2> 

    <form #signupForm="ngForm" (ngSubmit)="onSubmit(signupForm)" novalidate>
      <!-- Organization Name -->
      <div class="mb-3">
        <label for="orgName" class="form-label">Organization Name</label>
        <input type="text" id="orgName" name="orgName" class="form-control" placeholder="Enter organization name" ngModel required>
        <div *ngIf="signupForm.submitted && !signupForm.form.controls.orgName?.valid" class="text-danger">
          Organization Name is required.
        </div>
      </div>

      <!-- Contact and Email -->
      <div class="row">
        <div class="col-md-6 mb-3">
          <label for="contact" class="form-label">Contact</label>
          <input type="tel" id="contact" name="contact" class="form-control" placeholder="Enter contact number" ngModel required>
          <div *ngIf="signupForm.submitted && !signupForm.form.controls.contact?.valid" class="text-danger">
            Contact is required.
          </div>
        </div>
        <div class="col-md-6 mb-3">
          <label for="email" class="form-label">Email</label>
          <input type="email" id="email" name="email" class="form-control" placeholder="Enter email address" ngModel required>
          <div *ngIf="signupForm.submitted && !signupForm.form.controls.email?.valid" class="text-danger">
            Email is required.
          </div>
        </div>
      </div>

      <!-- Password and Confirm Password -->
      <div class="row">
        <div class="col-md-6 mb-3">
          <label for="password" class="form-label">Password</label>
          <input type="password" id="password" name="password" class="form-control" placeholder="Enter password" ngModel required>
          <div *ngIf="signupForm.submitted && !signupForm.form.controls.password?.valid" class="text-danger">
            Password is required.
          </div>
        </div>
        <div class="col-md-6 mb-3">
          <label for="confirmPassword" class="form-label">Confirm Password</label>
          <input type="password" id="confirmPassword" name="confirmPassword" class="form-control" placeholder="Confirm password" ngModel required>
          <div *ngIf="signupForm.submitted && !signupForm.form.controls.confirmPassword?.valid" class="text-danger">
            Confirm password is required.
          </div>
        </div>
      </div>

      <!-- Street Address -->
      <div class="mb-3">
        <label for="streetAddress" class="form-label">Street Address</label>
        <input type="text" id="streetAddress" name="streetAddress" class="form-control" placeholder="Enter street address" ngModel required>
        <div *ngIf="signupForm.submitted && !signupForm.form.controls.streetAddress?.valid" class="text-danger">
          Street Address is required.
        </div>
      </div>

      <!-- City and District -->
      <div class="row">
        <div class="col-md-6 mb-3">
          <label for="city" class="form-label">City</label>
          <input type="text" id="city" name="city" class="form-control" placeholder="Enter city" ngModel required>
          <div *ngIf="signupForm.submitted && !signupForm.form.controls.city?.valid" class="text-danger">
            City is required.
          </div>
        </div>
        <div class="col-md-6 mb-3">
          <label for="district" class="form-label">District</label>
          <input type="text" id="district" name="district" class="form-control" placeholder="Enter district" ngModel required>
          <div *ngIf="signupForm.submitted && !signupForm.form.controls.district?.valid" class="text-danger">
            District is required.
          </div>
        </div>
      </div>

      <!-- Block, Zip Code, and State -->
      <div class="row">
        <div class="col-md-4 mb-3">
          <label for="block" class="form-label">Block</label>
          <input type="text" id="block" name="block" class="form-control" placeholder="Enter block" ngModel required>
          <div *ngIf="signupForm.submitted && !signupForm.form.controls.block?.valid" class="text-danger">
            Block is required.
          </div>
        </div>
        <div class="col-md-4 mb-3">
          <label for="zipCode" class="form-label">Zip Code</label>
          <input type="text" id="zipCode" name="zipCode" class="form-control" placeholder="Enter zip code" ngModel required>
          <div *ngIf="signupForm.submitted && !signupForm.form.controls.zipCode?.valid" class="text-danger">
            Zip Code is required.
          </div>
        </div>
        <div class="col-md-4 mb-3">
          <label for="state" class="form-label">State</label>
          <input type="text" id="state" name="state" class="form-control" placeholder="Enter state" ngModel required>
