import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent implements OnInit {

  ngOnInit(): void {}

  signupForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.signupForm = this.fb.group({
      orgImage: [null],
      orgName: ['', Validators.required],
      contact: ['', [Validators.required, Validators.pattern('[0-9]{10}')]],
      email: ['', [Validators.required, Validators.email]],
      streetAddress: ['', Validators.required],
      city: ['', Validators.required],
      district: ['', Validators.required],
      block: ['', Validators.required],
      zipCode: ['', Validators.required],
      state: ['', Validators.required]
    });
  }

  onFileChange(event: any) {
    const file = event.target.files[0];
    this.signupForm.patchValue({
      orgImage: file
    });
    this.signupForm.get('orgImage')?.updateValueAndValidity();
  }

  onSubmit() {
    const formData = new FormData();
    Object.keys(this.signupForm.controls).forEach(key => {
      formData.append(key, this.signupForm.get(key)?.value);
    });

    // this.http.post('https://your-backend-url.com/api/signup', formData)
    //   .subscribe((response: any) => {
    //     console.log('Form submitted successfully', response);
    //   }, (error: any) => {
    //     console.error('Error submitting form', error);
    //   });

  }


}



<div class="container mt-5 mb-5">
  <div class="card shadow-lg p-4">
    <h2 class="text-center mb-4">Organization Signup</h2>

    <form>
      <!-- Organization Image -->
      <div class="mb-3 text-center">
        <label for="orgImage" class="form-label">Organization Image</label>
        <input type="file" class="form-control" id="orgImage" accept="image/*">
      </div>

      <!-- Organization Name -->
      <div class="mb-3">
        <label for="orgName" class="form-label"><i class="icon bi bi-building"></i>Organization Name</label>
        <input type="text" class="form-control" id="orgName" placeholder="Enter organization name">
      </div>

      <!-- Contact and Email -->
      <div class="row">
        <div class="col-md-6 mb-3">
          <label for="contact" class="form-label"><i class="icon bi bi-telephone"></i>Contact</label>
          <input type="tel" class="form-control" id="contact" placeholder="Enter contact number">
        </div>
        <div class="col-md-6 mb-3">
          <label for="email" class="form-label"><i class="icon bi bi-envelope"></i>Email</label>
          <input type="email" class="form-control" id="email" placeholder="Enter email address">
        </div>
      </div>

      <!-- Street Address -->
      <div class="mb-3">
        <label for="streetAddress" class="form-label"><i class="icon bi bi-geo-alt"></i>Street Address</label>
        <input type="text" class="form-control" id="streetAddress" placeholder="Enter street address">
      </div>

      <!-- City and District -->
      <div class="row">
        <div class="col-md-6 mb-3">
          <label for="city" class="form-label"><i class="icon bi bi-building"></i>City</label>
          <input type="text" class="form-control" id="city" placeholder="Enter city">
        </div>
        <div class="col-md-6 mb-3">
          <label for="district" class="form-label"><i class="icon bi bi-building"></i>District</label>
          <input type="text" class="form-control" id="district" placeholder="Enter district">
        </div>
      </div>

      <!-- Block, Zip Code, and State -->
      <div class="row">
        <div class="col-md-4 mb-3">
          <label for="block" class="form-label"><i class="icon bi bi-pin-map"></i>Block</label>
          <input type="text" class="form-control" id="block" placeholder="Enter block">
        </div>
        <div class="col-md-4 mb-3">
          <label for="zipCode" class="form-label"><i class="icon bi bi-postage"></i>Zip Code</label>
          <input type="text" class="form-control" id="zipCode" placeholder="Enter zip code">
        </div>
        <div class="col-md-4 mb-3">
          <label for="state" class="form-label"><i class="icon bi bi-flag"></i>State</label>
          <input type="text" class="form-control" id="state" placeholder="Enter state">
        </div>
      </div>

      <!-- Submit Button -->
      <div class="text-center mt-4">
        <button type="submit" class="btn btn-primary btn-lg" (click)="signUpForm()">Sign Up</button>
      </div>
    </form>
  </div>
</div>
