import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent implements OnInit {

  ngOnInit(): void {}

  signupForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.signupForm = this.fb.group({
      orgImage: [null],
      orgName: ['', Validators.required],
      contact: ['', [Validators.required, Validators.pattern('[0-9]{10}')]],
      email: ['', [Validators.required, Validators.email]],
      streetAddress: ['', Validators.required],
      city: ['', Validators.required],
      district: ['', Validators.required],
      block: ['', Validators.required],
      zipCode: ['', Validators.required],
      state: ['', Validators.required]
    });
  }

  onFileChange(event: any) {
    const file = event.target.files[0];
    this.signupForm.patchValue({
      orgImage: file
    });
    this.signupForm.get('orgImage')?.updateValueAndValidity();
  }

  onSubmit() {
    const formData = new FormData();
    Object.keys(this.signupForm.controls).forEach(key => {
      formData.append(key, this.signupForm.get(key)?.value);
    });
    // this.http.post('https://your-backend-url.com/api/signup', formData)
    //   .subscribe((response: any) => {
    //     console.log('Form submitted successfully', response);
    //   }, (error: any) => {


    //     console.error('Error submitting form', error);
    //   });

  }


}
<div class="container mt-5 mb-5">
  <div class="card shadow-lg p-4">
    <h2 class="text-center mb-4">Organization Signup</h2>

    <form [formGroup]="signupForm" (ngSubmit)="onSubmit()">
      <!-- Organization Image -->
      <div class="mb-3 text-center">
        <label for="orgImage" class="form-label">Organization Image</label>
        <input type="file" class="form-control" id="orgImage" (change)="onFileChange($event)">
      </div>

      <!-- Organization Name -->
      <div class="mb-3">
        <label for="orgName" class="form-label">Organization Name</label>
        <input type="text" formControlName="orgName" class="form-control" placeholder="Enter organization name">
      </div>

      <!-- Contact and Email -->
      <div class="row">
        <div class="col-md-6 mb-3">
          <label for="contact" class="form-label">Contact</label>
          <input type="tel" formControlName="contact" class="form-control" placeholder="Enter contact number">
        </div>
        <div class="col-md-6 mb-3">
          <label for="email" class="form-label">Email</label>
          <input type="email" formControlName="email" class="form-control" placeholder="Enter email address">
        </div>
      </div>

      <!-- Street Address -->
      <div class="mb-3">
        <label for="streetAddress" class="form-label">Street Address</label>
        <input type="text" formControlName="streetAddress" class="form-control" placeholder="Enter street address">
      </div>

      <!-- City and District -->
      <div class="row">
        <div class="col-md-6 mb-3">
          <label for="city" class="form-label">City</label>
          <input type="text" formControlName="city" class="form-control" placeholder="Enter city">
        </div>
        <div class="col-md-6 mb-3">
          <label for="district" class="form-label">District</label>
          <input type="text" formControlName="district" class="form-control" placeholder="Enter district">
        </div>
      </div>

      <!-- Block, Zip Code, and State -->
      <div class="row">
        <div class="col-md-4 mb-3">
          <label for="block" class="form-label">Block</label>
          <input type="text" formControlName="block" class="form-control" placeholder="Enter block">
        </div>
        <div class="col-md-4 mb-3">
          <label for="zipCode" class="form-label">Zip Code</label>
          <input type="text" formControlName="zipCode" class="form-control" placeholder="Enter zip code">
        </div>
        <div class="col-md-4 mb-3">
          <label for="state" class="form-label">State</label>
          <input type="text" formControlName="state" class="form-control" placeholder="Enter state">
        </div>
      </div>

      <!-- Submit Button -->
      <div class="text-center mt-4">
        <button type="submit" class="btn btn-primary btn-lg" [disabled]="signupForm.invalid">Sign Up</button>
      </div>
    </form>
  </div>
</div>
